async function updateIngredient(ingredientId, scientificBackground) {
  try {
    const result = await client
      .patch(ingredientId)
      .set({ scientificBackground })
      .commit();

    return result;
  } catch (error) {
    console.error(`âŒ æ›´æ–°å¤±æ•— (${ingredientId}):`, error.message);
    throw error;
  }
}

